import React, { useState, useEffect, useRef } from 'react'

export default function Canvas(props) {
  const [drawing, setDrawing] = useState(false)
  const [restart, setRestart] = useState(true)

  const canvasRef = useRef()
  const ctx = useRef()
  const converted = useRef()

  useEffect(() => {
    ctx = canvasRef.getContext('2d')
    // draw canvas
    ctx.fillStyle = "#17252A"
    ctx.fillRect(0, 0, 415, 415)

  }, [])

  // Reset if reset/start is clicked in parent.
  useEffect(() => {
    handleClear()
    if(props.ifStart === "reset"){
      setDrawing(true)
    } else {
      setDrawing(false)
    }
  }, [props.ifStart])

  // Stop when time is 0
  useEffect(() => {
    if(props.ifFinished){
      setDrawing(false)
    } 
  }, [props.ifFinished])


  // draw when moving mouse if draw = true
  function handleMouseMove(e) {
    const coords = [
      e.clientX - canvasRef.offsetLeft,
      e.pageY - canvasRef.offsetTop
    ]
      ctx.lineTo(...coords)
      ctx.stroke()
  }

  function startDrawing(e) {
    ctx.lineJoin = 'round'
    ctx.lineCap = 'round'
    ctx.lineWidth = 20
    // ctx.strokeStyle = "#3AAFA9"
    ctx.strokeStyle = "white"
    ctx.beginPath();
    // actual coordinates
    ctx.moveTo(
      e.clientX - canvasRef.offsetLeft,
      e.pageY - canvasRef.offsetTop
    )
  }


   //clear
  function handleClear() {
    ctx.clearRect(0, 0, 415, 415)
  }

  //stop
  function stopDrawing() {
    ctx.closePath()
  }

  // download
  // function download(){
  //   const dataURI = canvasRef.toDataURL();
  //   console.log(dataURI)
  //   converted.src = dataURI
  // }

  return (
    <div>
      {/* <button onClick ={download}> download </button> */}
      {/* <div className ="canvas"> <img ref={converted} src="" /></div> */}
      <div className ="canvas">
        <canvas
        ref={canvasRef}
        width={415}
        height={415}
        onMouseOver={startDrawing}
        onMouseOut={stopDrawing}
        onMouseMove={handleMouseMove}
        />
      </div>
    </div>
  )
}
