import React, { useState, useEffect, useRef } from 'react'

export default function Canvas(props) {
  const [drawing, setDrawing] = useState(true)

  const canvasRef = useRef()
  const ctx = useRef()

  useEffect(() => {
    ctx.current = canvasRef.current.getContext('2d')
    ctx.current.fillStyle = "#17252A"
    ctx.current.fillRect(0, 0, 400, 400)
  }, [])

  function handleMouseMove(e) {
    // actual coordinates
    const coords = [
      e.clientX - canvasRef.current.offsetLeft,
      e.pageY - canvasRef.current.offsetTop
    ]
    if (drawing) { 
      ctx.current.lineTo(...coords)
      ctx.current.stroke()
    }
    if (props.handleMouseMove) {
        props.handleMouseMove(...coords)
    }
  }

  function startDrawing(e) {

    ctx.current.lineJoin = 'round'
    ctx.current.lineCap = 'round'
    ctx.current.lineWidth = 20
    ctx.current.strokeStyle = "#3AAFA9"
    ctx.current.beginPath();
    // actual coordinates
    ctx.current.moveTo(
      e.clientX - canvasRef.current.offsetLeft,
      e.pageY - canvasRef.current.offsetTop
    )
    setDrawing(true)
  }
  function stopDrawing() {
    ctx.current.closePath()
    setDrawing(false)
  }

  // function handleClick(elem){
    // var box = elem.getBoundingClientRect();

    // var body = document.body;
    // var docEl = document.documentElement;

    // var scrollTop = window.pageYOffset || docEl.scrollTop || body.scrollTop;
    // var scrollLeft = window.pageXOffset || docEl.scrollLeft || body.scrollLeft;

    // var clientTop = docEl.clientTop || body.clientTop || 0;
    // var clientLeft = docEl.clientLeft || body.clientLeft || 0;

    // var top  = box.top +  scrollTop - clientTop;
    // var left = box.left + scrollLeft - clientLeft;
    // var obj =  { top: Math.round(top), left: Math.round(left) }
    // console.log(elem.pageY)
    // var rect = div.getBoundingClientRect();
  // }

  return (
    <div className ="canvas">
      <canvas
      ref={canvasRef}
      width={400}
      height={400}
      // onMouseDown={startDrawing}
      // onMouseOver={startDrawing}
      // onMouseUp={stopDrawing}
      // onMouseOut={stopDrawing}
      onMouseMove={handleMouseMove}
      // onMouseClick={handleMouseMove}

      // onMouseMove = {handleClick}
      />
    </div>
  )
}



