import axios from 'axios';

function Gallery(){

    const [artpieces, setArtpieces] = useState([])

    useEffect(()=> {
        axios.get('http://localhost:5000/artpieces/')
        .then(response => {
            setArtpieces(response.data)
            // console.log(response.data)
        })


        .catch((error) => {
          console.log(error);
        })   
    },[])

    // onClick={artpieceClick}
