    // }

    function deleteArtpiece(id){
        // axios.delete('http://localhost:5000/artpieces/'+id)
        // .then(response => { console.log(response.data)});

        // setArtpieces(
        //     artpieces.filter(el => el._id !== id)
        // )
        console.log(id)
    }



    const all = artpieces.map(item => { 
        return (
            <Artpiece item={item} clickEvent = {deleteArtpiece(item._id)}/>
        )
     })


    return (
        <div className ="gallery">
            <h1> GALLERY </h1>
            <div className ="gallery__artpieces">
                {all}
            </div>   
        </div>

    )
}

export default Gallery

function Artpiece(props){


    return (
        <div  key={props.item._id} className ="gallery__artpiece">
        <button onClick={props.clickEvent} className = "del">x</button>
        <img  src={props.item.uri} alt="artpiece" /> 
        <p>{props.item.title}</p>
    </div>
    )
}
