            setLimit(0)
            setClock(clock - 1)
         }
            //

            // setLimit(0)    
    }

    return (
        <div className = "countdown">
            <button onClick = {restart}> restart </button>
            <h1>{clock}s</h1>
    <button onClick = {stop}> {stopResume}</button>
        </div>

    )
}
