import React , {useState, useEffect, useRef} from 'react';
import axios from 'axios';

function Gallery(){

    const [artpieces, setArtpieces] = useState([])
    const galleryArtpiece = useRef()

    useEffect(()=> {
        axios.get('http://localhost:5000/artpieces/')
        .then(response => {
            setArtpieces(response.data)
            // console.log(response.data)
        })


        .catch((error) => {
          console.log(error);
        })   
    },[])

    function artpieceClick(){
       const artpieceDiv = galleryArtpiece.current
       artpieceDiv.classList.add(".size")
        console.log(artpieceDiv)
    }

    const all = artpieces.map(item => { 
            return (
                <div ref={galleryArtpiece} key={item._id} className ="gallery__artpiece">
                    <img onClick={artpieceClick}  src={item.uri} alt="artpiece" /> 
                    <p>{item.title}</p> 
                </div>)
     })


    return (
        <div className ="gallery">
            <h1> GALLERY </h1>
            <div className ="gallery__artpieces">
                {all}
            </div>   
        </div>

    )
}

export default Gallery
