function Countdown(){
    const [clock, setClock] = useState(60)
    const [limit, setLimit] = useState(0)
    const [stopResume, setStopResume] = useState("stop")

    useEffect(() => {   
        let timer = setTimeout(() => { if(clock>=limit){setClock(clock-1)} }, 1000);  
        return () => {
            clearTimeout(timer)
        }
        }, 
    [clock])

    function restart(){
        setClock(60)
        setLimit(0)
    }

